import React, { Component } from "react";

class Form extends Component {
    constructor() {
        super();
        this.state = {
            studentId: '',
            studentName: '',
            studentAge: '',
            studentGender: false,
            studentDateBirth: '',
            studentPlaceBirth: '',
            studentAddress: '',


        }
    }
    componentWillMount = () => { }


    com


    hideForm = () => {
        this.props.controlForm(false);
    }


    render() {
        let { listStudents } = this.props;
        let { id } = this.props;

        let studentId, studentName, studentAge, studentGender, studentDateBirth, studentPlaceBirth, studentAddress
        if (id === undefined) {
            studentId = '';
            studentName = '';
            studentAge = '';
            studentGender = '';
            studentDateBirth = '';
            studentPlaceBirth = '';
            studentAddress = '';
        }
        else {
            studentId = listStudents[id].studentId;
            studentName = listStudents[id].studentName;
            studentAge = listStudents[id].studentAge;
            studentGender = listStudents[id].studentGender ? "Nam" : "Nữ";
            studentDateBirth = listStudents[id].studentDateBirth;
            studentPlaceBirth = listStudents[id].studentPlaceBirth;
            studentAddress = listStudents[id].studentAddress;
        }


        let { actionName } = this.props
        let elementButton = ''
        if (actionName == "CreateStudent") {
            elementButton = <button type="submit" className="btn btn-primary me-2">
                Create
            </button>
        }
        else if (actionName == "UpdateStudent") {
            elementButton = <button type="submit" className="btn btn-primary me-2">
                Update
            </button>
        }


        return (
            <div className="col-5 grid-margin">
                <div className="card">
                    <div className="card-body">
                        <h3 className="card-title">Thông tin sinh viên</h3>
                        <i className="fa-regular fa-circle-xmark fa-xl icon-close" onClick={this.hideForm} />
                        <form className="form-sample">
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Mã sinh viên</label>
                                <div className="col-sm-9">
                                    <input type="text" className="form-control" onChange={this.changeState} defaultValue={this.state.studentId} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Tên sinh viên</label>
                                <div className="col-sm-9">
                                    <input type="text" className="form-control" defaultValue={this.state.studentName} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Tuổi</label>
                                <div className="col-sm-9">
                                    <input type="text" className="form-control" defaultValue={this.state.studentAge} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Giới tính</label>
                                <div className="col-sm-9">
                                    <select className="form-control" defaultValue={this.state.studentGender}>
                                        <option value={true}>Nam</option>
                                        <option value={false}>Nữ</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Ngày sinh</label>
                                <div className="col-sm-9">
                                    <input className="form-control" placeholder="dd/mm/yyyy" defaultValue={this.state.studentDateBirth} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Nơi sinh</label>
                                <div className="col-sm-9">
                                    <select className="form-control" defaultValue={this.state.studentPlaceBirth}>
                                        <option value={"hn"}>Hà Nội</option>
                                        <option value={"sg"}>TP. Hồ Chí Minh</option>
                                        <option value={"dn"}>Đà Nẵng</option>
                                        <option value={"qn"}>Quảng Ninh</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Địa chỉ</label>
                                <div className="col-sm-9">
                                    <textarea className="form-control" defaultValue={this.state.studentAddress} />
                                </div>
                            </div>
                            {elementButton}
                        </form>
                    </div>
                </div >
            </div >
        )
    }
}

export default Form
